// Remove these imports
// import fs from 'fs';
// import path from 'path';

// Use this instead for CRA:
const posts = {
  'first-post': {
    id: 'first-post',
    title: "My First Post",
    date: "2025-06-15",
    category: "Design",
    tags: ["web", "portfolio"],
    heroImage: "/images/design.jpg",
    content: `## Introduction\nThis is my **first post**!\n\n![Sample](/images/sample.jpg)`
  }
  // Add more posts here
};

export function getAllPosts() {
  return Object.values(posts);
}

export function getPostById(id) {
  return posts[id];
}
